DROP TABLE EK_CUSTOMER;
DROP TABLE EK_CUSTOMER_ADDRESS;
DROP TABLE EK_CUSTOMER_CART;
DROP TABLE EK_ORDER;
DROP TABLE EK_PRODUCT;


CREATE TABLE EK_CUSTOMER(
	EMAIL_ID VARCHAR(50),
	NAME VARCHAR(50) NOT NULL,
	PASSWORD VARCHAR(70) NOT NULL,
	PHONE_NUMBER VARCHAR(10) NOT NULL UNIQUE,
	constraint EK_CUSTOMER_EMAIL_ID_PK primary key ( EMAIL_ID )
);
CREATE TABLE EK_CUSTOMER_ADDRESS (
	ADDRESS_ID INT  NOT NULL,
	CONTACT_NUMBER VARCHAR(10) NOT NULL,
	LINE_1 VARCHAR (500) NOT NULL,
	LINE_2 VARCHAR (500),
	CITY VARCHAR(50) NOT NULL,
	STATE VARCHAR(50) NOT NULL,
	PIN VARCHAR(10) NOT NULL,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_ADDRESS_ID_PK primary key ( ADDRESS_ID )
);

ALTER TABLE EK_CUSTOMER_ADDRESS ADD CONSTRAINT EK_CUST_ADRS_EMAIL_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

CREATE TABLE EK_CUSTOMER_CART (
	CART_ID INT,
	PRODUCT_ID INT NOT NULL,
	QUANTITY SMALLINT,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_CART_ID_PK primary key (CART_ID)
);
ALTER TABLE EK_CUSTOMER_CART ADD CONSTRAINT EK_CUST_CARD_EMAIL_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

CREATE TABLE EK_PRODUCT(
	PRODUCT_ID INT ,
	NAME VARCHAR(500) NOT NULL,
	DESCRIPTION VARCHAR(1000) NOT NULL,
	CATEGORY VARCHAR(200) NOT NULL,
	BRAND VARCHAR(250) NOT NULL,
	PRICE INT NOT NULL,
	DISCOUNT DECIMAL(5,2) NOT NULL,
	QUANTITY SMALLINT NOT NULL,
	SELLER_EMAIL_ID VARCHAR(50),
	constraint EK_PRODUCT_ID_PK primary key ( PRODUCT_ID )
);
ALTER TABLE EK_PRODUCT ADD CONSTRAINT EK_CART_PRODUCT_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_CUSTOMER_CART( PRODUCT_ID );

CREATE TABLE EK_ORDER (
	ORDER_ID INT NOT NULL ,
	ORDER_NUMBER INT NOT NULL,
	DATE_OF_ORDER DATE NOT NULL,
	TOTAL_PRICE DECIMAL(12,2) NOT NULL,
	ADDRESS_ID INT NOT NULL,
	ORDER_STATUS VARCHAR(20) NOT NULL,
	PAYMENT_THROUGH VARCHAR(20) NOT NULL, 
	DATE_OF_DELIVERY DATE ,
    	CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_ORDER_ID_PK primary key (ORDER_ID)
);
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_CUST_ORDER_MAPP_EMAIL_ID_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ORD_ORDER_STATUS_CHK CHECK (ORDER_STATUS IN ('PLACED', 'CONFIRMED', 'DISPATCHED', 'IN_TRANSIT', 'OUT_FOR_DELIVERY', 'DELIVERED', 'CANCELLED', 'RETURNED'));
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ORD_PAYMENT_THROUGH_CHK CHECK (PAYMENT_THROUGH IN ('DEBIT_CARD', 'CREDIT_CARD', 'CASH_ON_DELIVERY'));
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ADDRESS_ID_FK FOREIGN KEY ( ADDRESS_ID ) REFERENCES EK_CUSTOMER_ADDRESS( ADDRESS_ID );








